import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import "./styles.scss";
import * as actions from "./../../../Actions";
import User from "./User";

const Index = () => {
  const usersAdmin = useSelector((state) => state.usersAdmin);
  const usersCustomer = useSelector((state) => state.users);
  const dispatch = useDispatch();
  const getUsersAdmin = () => dispatch(actions.acGetUserAdminRequest());
  const getUsersCustomer = () => dispatch(actions.acGetUsersRequest());

  var usersList = [...usersAdmin];
  usersList = [...usersList, usersCustomer];

  console.log(usersList);
  useEffect(() => {
    (async () => {
      await getUsersAdmin();
      await getUsersCustomer();
    })();
  }, []);

  const listUser = (users) => {
    return users.map((user, index) => {
      return <User user={user} key={index} />;
    });
  };

  return (
    <div className="container-fluid mt-5" id="manager-users">
      <div className="container">
        <div className="row">
          <div className="col-12 text-center">
            <h3 className="font-ubun">Manager Users</h3>
          </div>
          <div className="col-12">
            <table className="table">
              <thead>
                <tr>
                  <th>STT</th>
                  <th>Account</th>
                  <th>Password</th>
                  <th>Status</th>
                  <th>Role</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {/* {listUser(usersAdmin)}
                {listUser(usersCustomer)} */}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Index;
