import React, { useState } from "react";

import {
  Box,
  Grid,
  TextField,
  Button,
  FormControl,
  Input,
  InputLabel,
} from "@material-ui/core";
import HeaderTask from "./../Home/Tasks/HeaderTask";

export default function Profile() {
  const [inputValue, setInputValue] = useState({
    username: "",
    email: "",
    firstName: "",
    lastName: "",
    city: "",
    country: "",
    postalCode: "",
    about: "",
  });

  const listInput = [
    {
      label: "Username",
      name: "username",
      width: 3,
      value: inputValue.username,
    },
    {
      label: "Email Address",
      name: "email",
      width: 3,
      value: inputValue.email,
    },
    {
      label: "First Name",
      width: 6,
      name: "firstName",
      value: inputValue.firstName,
    },
    {
      label: "Last Name",
      width: 6,
      name: "lastName",
      value: inputValue.lastName,
    },
    {
      label: "City",
      width: 4,
      name: "city",
      value: inputValue.city,
    },
    {
      label: "Country",
      width: 4,
      name: "country",
      value: inputValue.country,
    },
    {
      label: "Postal Code",
      width: 4,
      name: "postalCode",
      value: inputValue.postalCode,
    },
    {
      label: "About me",
      width: 12,
      name: "about",
      value: inputValue.about,
    },
  ];

  const onChange = (e) => {
    console.log(e.target.name, e.target.value);
  };

  return (
    <Box className="body-admin m-0 pt-5 bgr-admin" sx={{ minHeight: "100vh" }}>
      <Grid container spacing={1} className="ps-4 w-100">
        <Grid item xs={8}>
          <Box className="box">
            <HeaderTask
              bgr="badge-purple"
              title="Edit Profile"
              position="start"
              caption="Complete your profile"
            />
            <Grid container spacing={2} className="py-4 px-3">
              <Grid item xs={6}>
                <TextField
                  label="Company"
                  defaultValue="How2Code"
                  disabled
                  variant="standard"
                  className="w-100"
                />
              </Grid>
              {listInput.map((item, index) => {
                return (
                  <Grid key={index} item xs={item.width}>
                    <FormControl>
                      <InputLabel>{item.label}</InputLabel>
                      <Input
                        className="w-100"
                        value={item.value}
                        onChange={onChange}
                        name={item.name}
                      />
                    </FormControl>
                  </Grid>
                );
              })}
              <Grid item xs={12} className="mt-3">
                <Button variant="contained" color="secondary">
                  UPDATE PROFILE
                </Button>
              </Grid>
            </Grid>
          </Box>
        </Grid>
        <Grid item xs={4}></Grid>
      </Grid>
    </Box>
  );
}
