import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import "./styles.scss";
import callApi from "./../../../utils/apiCaller";
import * as actions from "./../../../Actions";
import User from "./User";

const Index = () => {
  const [usersAdmin, setUsersAdmin] = useState([]);
  const [usersCustomer, setUsersCustomer] = useState([]);

  useEffect(() => {
    (async () => {
      callApi("usersAdmin", "GET", null).then((res) => setUsersAdmin(res.data));
      callApi("users", "GET", null).then((res) => setUsersCustomer(res.data));
    })();
  }, []);

  const newList = usersAdmin.concat(usersCustomer);

  var listUsers = (users) => {
    return users.map((user, index) => {
      return (
        <User
          user={user}
          key={index}
          removeAccount={removeAccount}
          index={index + 1}
        />
      );
    });
  };

  const removeAccount = (id, role) => {
    if (role === "admin") {
      //admin
      console.log("admin");
    } else {
      //customer
      console.log("Customer");
    }
  };

  return (
    <div className="container-fluid mt-5" id="manager-users">
      <div className="container">
        <div className="row">
          <div className="col-12 text-center">
            <h3 className="font-ubun">Manager Users</h3>
          </div>
          <div className="col-12">
            <table className="table">
              <thead>
                <tr>
                  <th>STT</th>
                  <th>Email</th>
                  <th colSpan="2">Password</th>
                  <th>Role</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>{listUsers(newList)}</tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Index;
