import React, { Component } from "react";
// import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import * as actions from "../../../Actions";
import "./styles.scss";

class Avatar extends Component {
  constructor(props) {
    super(props);
    this.fileRef = React.createRef();
  }

  onChooseAvatar = () => {
    this.fileRef.current.click();
  };

  render() {
    return (
      <div className="container-fluid" id="login-form">
        <div className="login-form-content p-0">
          <div className="row">
            <div className="col bg-danger">
              <div className="row">
                <div className="img col-12">
                  <img
                    src="https://image.flaticon.com/icons/png/128/236/236832.png"
                    alt=""
                  />
                </div>
                <div className="choose-avatar col-12">
                  <input
                    type="file"
                    style={{ display: none }}
                    ref={this.fileRef}
                    name="file"
                  />
                  <button
                    className="btn shadow-none"
                    onClick={this.onChooseAvatar}
                  >
                    Choose Avatar
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    users: state.usersAdmin,
  };
};

const mapDispatchToProps = (dispatch, props) => {
  return {
    onGetUsers: () => {
      dispatch(actions.acGetUserAdminRequest());
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Avatar);
