import React, { Component, Fragment, useState } from "react";
import { Link } from "react-router-dom";
import "./styles.scss";

const subMenuProducts = [
  {
    to: "/",
    image: "https://www.aorus.com/assets/img/Motherboard.18fe2f57.png",
    label: "MOTHERBOARD",
  },
  {
    to: "/",
    image: "https://www.aorus.com/assets/img/Graphics.6ca7d8bf.png",
    label: "GRAPHICS CARDS",
  },
  {
    to: "/",
    image: "https://www.aorus.com/assets/img/Laptop.e77d5ad1.png",
    label: "LAPTOPS",
  },
  {
    to: "/",
    image: "https://www.aorus.com/assets/img/Monitor.b4e0aada.png",
    label: "MONITORS",
  },
  {
    to: "/",
    image: "https://www.aorus.com/assets/img/Desktop-PC.ab83c481.png",
    label: "DESKTOP PC",
  },
  {
    to: "/",
    image: "https://www.aorus.com/assets/img/Peripherals.9f53da7d.png",
    label: "PC PERIPHERALS",
  },
  {
    to: "/",
    image: "https://www.aorus.com/assets/img/Components.9dcdfdc0.png",
    label: "PC COMPONENTS",
  },
];

const subMenuExplore = [
  {
    to: "/",
    label: "NEWS",
  },
  {
    to: "/",
    label: "EVENTS",
  },
  {
    to: "/",
    label: "BLOG",
  },
  {
    to: "/",
    label: "WALLPAPER",
  },
];

const subMenuService = [
  {
    to: "/",
    label: "WARRANTY INFORMATION",
  },
  {
    to: "/",
    label: "PRODUCT REGISTRATION",
  },
];

const subMenuMember = [
  {
    to: "/",
    label: "WHY JOIN?",
  },
  {
    to: "/",
    label: "MEMBERSHIP LEVELS",
  },
  {
    to: "/",
    label: "AORUS POINTS & REWADS",
  },
  {
    to: "/",
    label: "ACHIVEMENT BAGGES",
  },
  {
    to: "/",
    label: "MEMBER LEADERBOARD",
  },
  {
    to: "/",
    label: "MEMBER FAQS",
  },
];

class HeaderRight extends Component {
  constructor(props) {
    super(props);
    this.state = {
      subToggle: false,
      toggle: false,
    };
  }

  subMenupProduct = subMenuProducts.map((item, index) => {
    return (
      <li key={index} className="drop-item">
        <Link to={item.to}>
          <img src={item.image} alt="" />
        </Link>
        <div className="item-title">{item.label}</div>
      </li>
    );
  });

  subMenuExplore = subMenuExplore.map((item, index) => {
    return (
      <li key={index} className="drop-item">
        <Link to={item.to} className="nav-link">
          {item.label}
        </Link>
      </li>
    );
  });

  subMenuService = subMenuService.map((item, index) => {
    return (
      <li key={index} className="drop-item">
        <Link to={item.to} className="nav-link">
          {item.label}
        </Link>
      </li>
    );
  });
  subMenuMember = subMenuMember.map((item, index) => {
    return (
      <li key={index} className="drop-item">
        <Link to={item.to} className="nav-link" href="">
          {item.label}
        </Link>
      </li>
    );
  });

  subCanvasProduct = subMenuProducts.map((item, index) => {
    return (
      <Link to={item.to} key={index}>
        {item.label}
      </Link>
    );
  });

  subCanvasExplore = subMenuExplore.map((item, index) => {
    return (
      <Link to={item.to} key={index}>
        {item.label}
      </Link>
    );
  });
  subCanvasService = subMenuService.map((item, index) => {
    return (
      <Link to={item.to} key={index}>
        {item.label}
      </Link>
    );
  });
  subCanvasMember = subMenuMember.map((item, index) => {
    return (
      <Link to={item.to} key={index}>
        {item.label}
      </Link>
    );
  });

  onOpenSubMenu = (e) => {
    console.log(subNav);
  };

  render() {
    const [toggle, setToggle] = useState(false);

    return (
      <Fragment>
        <div>
          <button className="btn text-white fs-5 shadow-none d-block d-xl-none far fa-bars"></button>
          <div className="menu-canvas">
            <div className="canvas-header d-flex ms-2 align-items-center">
              <button className="btn text-white shadow-none canvas-close fas fa-times"></button>
            </div>
            <div className="canvas-content">
              <ul className="list">
                <li onClick={this.setToggle(true)} className="list-item mb-2">
                  <div className="item-title">
                    <span>PRODUCTS</span>
                    <i className="fas fa-chevron-down"></i>
                  </div>
                  <div className="sub-menu d-flex flex-column">
                    {this.subCanvasProduct}
                  </div>
                </li>
                <li onClick={this.onOpenSubMenu} className="list-item mb-2">
                  <div className="item-title">
                    <span>EXPLORE</span>
                    <i className="fas fa-chevron-down"></i>
                  </div>
                  <div className="sub-menu d-flex flex-column">
                    {this.subCanvasExplore}
                  </div>
                </li>
                <li onClick={this.onOpenSubMenu} className="list-item mb-2">
                  <div className="item-title">
                    <span>SERVICE</span>
                    <i className="fas fa-chevron-down"></i>
