import React, { Component } from "react";
// import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import * as actions from "../../../Actions";
import "./styles.scss";

class Avatar extends Component {
  constructor(props) {
    super(props);
    this.fileRef = React.createRef();
  }

  onChooseAvatar = () => {
    this.fileRef.current.click();
  };

  render() {
    return (
      <div className="container-fluid avatar-page" id="login-form">
        <div className="login-form-content p-0">
          <div className="row align-items-center w-100">
            <div className="col-6 avatar-left ">
              <div className="img mb-3 text-center">
                <img
                  src="https://image.flaticon.com/icons/png/128/236/236832.png"
                  alt=""
                  className="avatar rounded-circle "
                />
              </div>
              <div className="choose-avatar text-center">
                <input
                  type="file"
                  className="d-none"
                  ref={this.fileRef}
                  name="file"
                />
                <button
                  className="btn border shadow-none "
                  onClick={this.onChooseAvatar}
                >
                  Choose Avatar
                </button>
              </div>
            </div>
            <div className="col-6 text-center">
              <div className="display-5 fw-bold">Infomation</div>
              <form action="">
                <div className="input-group mb-4">
                  <input
                    type="text"
                    name="firstName"
                    aria-label="First name"
                    placeholder="First Name"
                    className="form-control shadow-none"
                  />
                  <input
                    type="text"
                    name="lastName"
                    aria-label="Last name"
                    placeholder="Last Name"
                    className="form-control shadow-none"
                  />
                </div>
                <div className="input-group mb-4">
                  <input
                    type="text"
                    name="address"
                    className="form-control shadow-none"
                    placeholder="Address"
                  />
                </div>
                <div className="input-group mb-4">
                  <input
                    type="text"
                    name="dob"
                    className="form-control shadow-none"
                    placeholder="Date of Birth"
                  />
                </div>
                <div className="input-group mb-4">
                  <input
                    type="text"
                    name="phone"
                    className="form-control shadow-none"
                    placeholder="Telephone Number"
                  />
                </div>
                <button className="btn btn-success w-50">Save</button>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    users: state.usersAdmin,
  };
};

const mapDispatchToProps = (dispatch, props) => {
  return {
    onGetUsers: () => {
      dispatch(actions.acGetUserAdminRequest());
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Avatar);
