import React, { Component } from "react";
import { Link } from "react-router-dom";
import "./styles.scss";
import CheckIcon from "@material-ui/icons/Check";

class Product extends Component {
  constructor(props) {
    super(props);
    this.state = {
      check: false,
    };
  }

  onAddToCart = (item) => {
    this.props.onAddToCart(item);
  };

  checkCarts = () => {
    let { carts, item } = this.props;

    carts.some((product) => product.id === item.id)
      ? this.setState({ check: true })
      : this.setState({ check: false });
  };

  render() {
    var { item, match, carts } = this.props;
    var { check } = this.state;
    console.log(carts);
    return (
      <div className="col product-item">
        <div className="bgr">
          <img src={item.image} alt="" />
          <div className="item-caption font-family-Ti">{item.name}</div>
          <div className="button-task">
            <Link
              to={`${match.path}/${item.id}/KeyFeatures`}
              className="btn rounded-0 button-link shadow-none me-3 font-family-Ti"
            >
              Learn More
            </Link>
            <button
              onClick={() => this.onAddToCart(item)}
              className="btn rounded-0 button-add shadow-none font-family-Ti"
            >
              {check && <CheckIcon />}
              Compare
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default Product;
