import * as types from "./../Constant/ActionTypes";

var initialState = [];

const cart = (state = initialState, action) => {
  switch (action.type) {
    case types.ADD_TO_CART:
      state = action.product;
      console.log(state);
      return [...state];
    default:
      return [...state];
  }
};

export default cart;
