import React, { Component } from "react";
import "./styles.scss";
import DetailNav from "./../DetailNav/DetailNav";
import Breadcumb from "./../../LoginPage/ContentLogin/Breadcumb/Breadcumb";

class Specification extends Component {
  constructor(props) {
    super(props);
    this.state = {
      sticky: false,
    };
  }

  componentDidMount() {
    window.addEventListener("scroll", this.sticky);
  }

  sticky = () => {
    if (window.scrollY > 172) {
      this.setState({ sticky: true });
    } else {
      this.setState({ sticky: false });
    }
  };
  render() {
    return (
      <div className="container-fluid p-0">
        <Breadcumb
          to="/"
          toBack="Home"
          to1="/GraphicsCard"
          toBack1="Graphics Cards"
          current="AORUS GeForce RTX™ 3090 XTREME WATERFORCE 24G"
          bgr="bgr-black"
        />
        <DetailNav sticky={this.sticky} />
        <h3>AORUS GeForce RTX™ 3090 XTREME WATERFORCE 24G</h3>
        <div className="container"></div>
      </div>
    );
  }
}

export default Specification;
