import React, { Component } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import * as actions from "./../../../Actions";
import "./styles.scss";

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      info: {},
      err: {},
    };
  }

  componentDidMount() {
    this.props.onGetUsers();
  }

  onChange = (e) => {
    var { info } = this.state;
    var name = e.target.name;
    var value = e.target.value;

    info[name] = value;

    this.setState({
      info,
    });
  };

  handleErr = () => {
    var { info, err } = this.state;
    var { users } = this.props;
    var check = true;

    if (!info.email) {
      err.email = "Email is required";
      check = false;
    } else err.email = "";

    if (!info.password) {
      err.password = "Password is required";
      check = false;
    } else err.password = "";

    if (
      users.every(
        (user) => user.email !== info.email || user.password !== info.password
      )
    ) {
      err.email = "Email or password is incorrect";
      check = false;
    } else err.email = "";

    this.setState({ err });

    return check;
  };

  onLogged = (e) => {
    e.preventDefault();
    if (this.handleErr()) {
    }
  };

  render() {
    var { info, err } = this.state;
    return (
      <div className="container-fluid" id="login-form">
        <div className="login-form-content">
          <div className="row w-100">
            <div className="col">
              <img
                src="https://colorlib.com/etc/lf/Login_v1/images/img-01.png"
                alt=""
              />
            </div>
            <div className="col text-center form">
              <h2 className="fw-bold mb-5">Member Login</h2>
              <form action="" onSubmit={this.onLogged}>
                <div className="input-group wapper-input mb-3">
                  <i className="fas fa-envelope" />
                  <input
                    type="email"
                    className="form-control shadow-none "
                    name="email"
                    value={info.email}
                    onChange={this.onChange}
                    placeholder="Email"
                    id="email"
                  />
                </div>
                <div className="input-group wapper-input mb-3">
                  <i className="fas fa-lock" />
                  <input
                    type="password"
                    className="form-control shadow-none "
                    name="password"
                    value={info.password}
                    onChange={this.onChange}
                    placeholder="Password"
                    id="password"
                  />
                </div>
                <p className="text-danger">
                  {err.email ? err.email : null}
                  {err.password ? err.password : null}
                </p>
                <button type="submit" className="btn btn-submit mt-4">
                  LOGIN
                </button>
                <div className="forgot-1 mt-2">
                  <span>
                    Forgot{" "}
                    <Link className="text-decoration-none" to="/login-admin">
                      Username/{" "}
                    </Link>
                    <Link className="text-decoration-none" to="/login-admin">
                      Password?
                    </Link>
                  </span>
                </div>
                <Link
                  to="/register-admin"
                  className="create-account d-block mt-5 text-decoration-none"
                >
                  Create your Account <i className="fal fa-arrow-right" />
                </Link>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    users: state.usersAdmin,
  };
};

const mapDispatchToProps = (dispatch, props) => {
  return {
    onGetUsers: () => {
      dispatch(actions.acGetUserAdminRequest());
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Login);
