import * as types from "./../Constant/ActionTypes";

var initialState = [];

const cart = (state = initialState, action) => {
  switch (action.type) {
    case types.GET_ALL_CART:
      state = [...action.products];
      return [...state];
    case types.ADD_TO_CART:
      state.push(action.product);
      return [...state];
    default:
      return [...state];
  }
};

export default cart;
