import React, { Component } from "react";
import InputSignIn from "./InputSignIn/InputSignIn";
import "./styles.scss";
import { connect } from "react-redux";
import * as action from "./../../../../Actions/index";

class SignInForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      info: [],
      err: {},
    };
  }

  onChangeLogin = () => {
    this.props.onChangeLogin();
  };

  onRegister = (data) => {
    var { info, err } = this.state;
    info.push(data);
    this.setState({
      info,
    });
    console.log(info);
    this.handleErr();
    if (err.email || err.username || err.password || err.repassword) {
      return;
    }
    console.log(info);
  };

  render() {
    var { changeLogin, match } = this.props;
    var url = match.url;
    var slug = changeLogin ? "register" : "login";
    return (
      <div className="container-fluid px-0 py-5" id="wapper-login">
        <div className="content-box">
          <div className="container-fluid">
            <div className="row">
              <InputSignIn
                onChangeLogin={this.onChangeLogin}
                url={url}
                slug={slug}
                onRegister={this.onRegister}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    changeLogin: state.changeLogin,
    listRegister: state.register,
  };
};

const mapDispatchToProps = (dispatch, props) => {
  return {
    onChangeLogin: () => {
      dispatch(action.onChangeLogin());
    },
    onRegister: () => {
      dispatch(action.acOnRegister());
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(SignInForm);
