import React, { useState, useEffect } from "react";

const Index = (props) => {
  const [number, setNumber] = useState(null);
  const [changePassword, setChangePassword] = useState("");

  useEffect(() => {
    setChangePassword(props.user.password);
  }, []);

  const changePass = () => {
    var password = props.user.password;
    var passwordHide = "";

    for (let i = 0; i < password.length; i++) {
      passwordHide += "*";
    }

    return passwordHide;
  };

  const onChangeHide = (index) => {
    setNumber(index);
  };

  return (
    <tr>
      <td>{props.index}</td>
      <td>{props.user.email}</td>
      <td>
        {/* {number === props.index ? props.user.password : changePass()} */}
        <input type="text" name="password" value={props.user.password} />
      </td>
      <td>
        <button
          onClick={() => onChangeHide(props.index)}
          className="btn shadow-none btn-eye fas fa-eye"
        />
      </td>
      <td>
        <span
          className={
            props.user.role
              ? "p-1 bg-warning rounded"
              : "p-1 bg-secondary rounded text-white"
          }
        >
          {props.user.role ? "Admin" : "Customer"}
        </span>
      </td>
      <td>
        <button className="btn shadow-none fas fa-edit"></button>
        <button
          onClick={() => props.removeAccount(props.user.id, props.user.role)}
          className="btn shadow-none fas fa-trash"
        ></button>
      </td>
    </tr>
  );
};

export default Index;
