import React, { Component } from "react";
import "./styles.scss";
import callApi from "./../../../../utils/apiCaller";
import { connect } from "react-redux";
import * as actions from "./../../../../Actions/index";

class Functions extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: "",
      name: "",
      price: "",
      des: "",
      image: null,
      imageLink: "",
      status: true,
      aorus: false,
      nvidia: false,
      amd: false,
      preview: "",
      gp: "",
      cc: "",
      ss: "",
      o: "",
      pc: "",
      rp: "",
      op: "",
      d: "",
      pf: "",
      cs: "",
      mv: "",
      dmr: "",
      cb: "",
      mbGB: "",
      mb: "",
      mt: "",
      msize: "",
      mc: "",
      ccores: "",
    };
  }

  componentDidMount() {
    var { match } = this.props;
    var id = match.params.id;
    if (id) {
      callApi(`products/${id}`, "GET", null).then((res) => {
        var product = res.data;
        this.setState({
          id: product.id,
          name: product.name,
          preview: product.image,
          image: product.image,
          imageLink: product.image,
          status: product.status,
          aorus: product.aorus,
          nvidia: product.state.nvidia,
          amd: product.amd,
          gp: product.gp,
          cc: product.cc,
          ss: product.ss,
          o: product.o,
          pc: product.pc,
          rp: product.rp,
          op: product.op,
          d: product.d,
          pf: product.pf,
          cs: product.cs,
          mv: product.mv,
          dmr: product.dmr,
          cb: product.cb,
          mbGB: product.mbGB,
          mb: product.mb,
          mt: product.mt,
          msize: product.msize,
          mc: product.mc,
          ccores: product.ccores,
        });
      });
    }
  }

  onChange = (e) => {
    var target = e.target;
    var name = target.name;
    var value = target.type === "checkbox" ? target.checked : target.value;
    this.setState({
      [name]: value,
    });
  };

  onUpFiles = (e) => {
    this.setState({
      image: e.target.files[0],
    });
    var reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onloadend = () => {
      this.setState({ preview: reader.result, image: reader.result });
    };
  };
  onSave = (e) => {
    e.preventDefault();
    console.log(this.state.aorus, this.state.amd, this.state.nvidia);
  };

  // onSave = (e) => {
  //   e.preventDefault();
  //   var { history } = this.props;
  //   var {
  //     id,
  //     name,
  //     price,
  //     des,
  //     image,
  //     status,
  //     aorus,
  //     nvidia,
  //     amd,
  //     imageLink,
  //     gp,
  //     cc,
