  //     o,
  //     pc,
  //     rp,
  //     op,
  //     d,
  //     pf,
  //     cs,
  //     mv,
  //     dmr,
  //     cb,
  //     mbGB,
  //     mb,
  //     mt,
  //     msize,
  //     mc,
  //     ccores,
  //   } = this.state;
  //   var product = {};
  //   if (id) {
  //     product = {
  //       id: id,
  //       name: name,
  //       price: price,
  //       des: des,
  //       image: image ? image : imageLink,
  //       status: status,
  //       aorus: aorus,
  //       nvidia: nvidia,
  //       amd: amd,
  //       gp: gp,
  //       cc: cc,
  //       ss: ss,
  //       o: o,
  //       pc: pc,
  //       rp: rp,
  //       op: op,
  //       d: d,
  //       pf: pf,
  //       cs: cs,
  //       mv: mv,
  //       dmr: dmr,
  //       cb: cb,
  //       mbGB: mbGB,
  //       mb: mb,
  //       mt: mt,
  //       msize: msize,
  //       mc: mc,
  //       ccores: ccores,
  //     };
  //     this.props.onUpdateProduct(product);
  //     history.goBack();
  //   } else {
  //     product = {
  //       id: id,
  //       name: name,
  //       price: price,
  //       des: des,
  //       image: image ? image : imageLink,
  //       status: status,
  //       aorus: aorus,
  //       nvidia: nvidia,
  //       amd: amd,
  //       gp: gp,
  //       cc: cc,
  //       ss: ss,
  //       o: o,
  //       pc: pc,
  //       rp: rp,
  //       op: op,
  //       d: d,
  //       pf: pf,
  //       cs: cs,
  //       mv: mv,
  //       dmr: dmr,
  //       cb: cb,
