  //       mb: mb,
  //       mt: mt,
  //       msize: msize,
  //       mc: mc,
  //       ccores: ccores,
  //     };
  //     this.props.onUpProduct(product);
  //     history.goBack();
  //   }
  // };

  render() {
    var {
      id,
      gp,
      cc,
      ss,
      o,
      pc,
      rp,
      op,
      d,
      pf,
      cs,
      mv,
      dmr,
      cb,
      mbGB,
      mb,
      mt,
      msize,
      mc,
      ccores,
    } = this.state;
    return (
      <div className="container mt-3" id="functions">
        <div className="title text-center">
          <div className="display-5 font-family-Ad">
            {id ? "UPDATE PRODUCT" : "ADD PRODUCT"}
          </div>
        </div>
        <form action="" onSubmit={this.onSave} className="form-control">
          <div className="mb-3">
            <label className="label-add" htmlFor="name-product">
              Name:
            </label>
            <input
              type="text"
              id="name-product"
              className="shadow-none form-control"
              name="name"
              value={this.state.name}
              onChange={this.onChange}
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              Price:
            </label>
            <input
              type="number"
              id="price-product"
              className="shadow-none form-control"
              name="price"
              value={this.state.price}
              onChange={this.onChange}
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              Graphics processing:
            </label>
            <input
              type="text"
              name="gp"
              value={gp}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              Core Clock:
            </label>
            <input
              type="text"
              name="cc"
              value={cc}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              SLI Support:
            </label>
            <input
              type="text"
              name="ss"
              value={ss}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              Output:
            </label>
            <input
              type="text"
              name="o"
              value={o}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              Power Connectors:
            </label>
            <input
              type="text"
              name="pc"
              value={pc}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              Recommended PSU:
            </label>
            <input
              type="text"
              name="rp"
              value={rp}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              OpenGL:
            </label>
            <input
              type="text"
              name="op"
              value={op}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              DirectX:
            </label>
            <input
              type="text"
              name="d"
              value={d}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              PCB Form:
            </label>
            <input
              type="text"
              name="pf"
              value={pf}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              Card Size:
            </label>
            <input
              type="text"
              name="cs"
              value={cs}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              Multi-view:
            </label>
            <input
              type="text"
              name="mv"
              value={mv}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              Digital max resolution:
            </label>
            <input
              type="text"
              name="dmr"
              value={dmr}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              Card Bus:
            </label>
            <input
              type="text"
              name="cb"
              value={cb}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              Memory Bandwidth (GB/sec):
            </label>
            <input
              type="text"
              name="mbGB"
              value={mbGB}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              Memory Bus:
            </label>
            <input
              type="text"
              name="mb"
              value={mb}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              Memory Type:
            </label>
            <input
              type="text"
              name="mt"
              value={mt}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              Memory Size:
            </label>
            <input
              type="text"
              name="msize"
              value={msize}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              Memory Clock:
            </label>
            <input
              type="text"
              name="mc"
              value={mc}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              CUDA® Cores:
            </label>
            <input
              type="text"
              name="ccores"
              value={ccores}
              onChange={this.onChange}
              className="shadow-none form-control"
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="description-product">
              Description:
            </label>
            <textarea
              id=""
              className="w-100"
              cols="30"
              rows="10"
              name="des"
              value={this.state.des}
              onChange={this.onChange}
            ></textarea>
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="img-product">
              Image Address:
            </label>
            <input
              type="file"
              id="img-product"
              className="shadow-none form-control"
              multiple
              onChange={this.onUpFiles}
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="img-product">
              Image Link:
            </label>
            <input
              type="text"
              id="img-product"
              className="shadow-none form-control"
              name="imageLink"
              multiple
              value={this.state.imageLink}
              onChange={this.onChange}
            />
          </div>
          <img src={this.state.preview} alt="" />
          <div className="mb-3">
            <label className="label-add me-2" htmlFor="status">
              Status
            </label>
            <input
              type="checkbox"
              className=""
              name="status"
              value={this.state.status}
              onChange={this.onChange}
              id="status"
              checked={this.state.status}
            />
          </div>
          <div className="mb-3 d-flex justify-content-start">
            <label className="label-add me-2" htmlFor="">
              Category:
            </label>
            <div className="input-group d-flex align-items-center">
              <label htmlFor="aorus" className="label-add me-2">
                AORUS
              </label>
              <input
                type="radio"
                className=""
                name="aorus"
                value={this.state.aorus}
                onChange={this.onChange}
                id="aorus"
                checked={this.state.aorus}
              />
            </div>
            <div className="input-group d-flex align-items-center">
              <label htmlFor="nvidia" className="label-add me-2">
                NVIDIA
              </label>
              <input
                type="radio"
                className=""
                name="aorus"
                value={this.state.nvidia}
                onChange={this.onChange}
                id="nvidia"
                checked={this.state.nvidia}
              />
            </div>
            <div className="input-group d-flex align-items-center">
              <label htmlFor="amd" className="label-add me-2">
                AMD
              </label>
              <input
                type="radio"
                className=""
                name="aorus"
                value={this.state.amd}
                onChange={this.onChange}
                id="amd"
                checked={this.state.amd}
              />
            </div>
          </div>
          <div className="mb-3">
            <button
              onClick={() => this.props.history.push("/admin")}
              className="btn btn-primary me-3 mb-3"
            >
              Go Back
            </button>
            <button type="submit" className="btn btn-success mb-3">
              Save
            </button>
          </div>
        </form>
      </div>
    );
  }
}

