import React, { Component } from "react";
import InputSignIn from "./InputSignIn/InputSignIn";
import "./styles.scss";
import { connect } from "react-redux";
import * as action from "./../../../../Actions/index";

class SignInForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      info: [],
    };
  }

  onChangeLogin = () => {
    this.props.onChangeLogin();
  };

  onRegister = (data) => {
    var { listRegister } = this.props;
    listRegister.forEach((item) => {
      if (item.email === data.email || item.username === data.username) {
        console.log("trung roi");
        return;
      }
    });
    console.log(data);
    this.props.onRegister(data);
  };

  render() {
    var { changeLogin, match } = this.props;
    console.log(this.props.listRegister);
    var url = match.url;
    var slug = changeLogin ? "register" : "login";
    return (
      <div className="container-fluid px-0 py-5" id="wapper-login">
        <div className="content-box">
          <div className="container-fluid">
            <div className="row">
              <InputSignIn url={url} slug={slug} onRegister={this.onRegister} />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    changeLogin: state.changeLogin,
    listRegister: state.register,
  };
};

const mapDispatchToProps = (dispatch, props) => {
  return {
    onRegister: (info) => {
      dispatch(action.acOnRegister(info));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(SignInForm);
