import React, { Component } from "react";

class Pagination extends Component {
  constructor(props) {
    super(props);
    this.state = {
      pageNumber: [],
    };
  }

  page = () => {
    var { postsPerPage, totalProduct } = this.props;
    var pageNumber = [];

    for (let i = 1; i <= Math.ceil(totalProduct / postsPerPage); i++) {
      pageNumber.push(i);
    }
    this.setState({ pageNumber });
  };

  liElement = () => {
    var { pageNumber } = this.state;
    return pageNumber.map((number) => {
      return (
        <li key={number} className="page-item px-0">
          <button className="page-link">{number}</button>
        </li>
      );
    });
  };

  render() {
    console.log(this.state.pageNumber);
    var { postsPerPage, totalProduct } = this.props;
    return (
      <nav aria-label="Page navigation example">
        <ul className="pagination">{this.liElement}</ul>
      </nav>
    );
  }
}

export default Pagination;
