import React, { Component } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import InputLogin from "./IputLogin/InputLogin";
import InputSignIn from "./InputSignIn/InputSignIn";
import "./styles.scss";
import { connect } from "react-redux";
import * as action from "./../../../../Actions/index";

class LoginForm extends Component {
  onChangeLogin = () => {
    this.props.onChangeLogin();
  };

  render() {
    var { changeLogin, match } = this.props;
    var url = match.url;
    var slug = changeLogin ? "register" : "login";
    return (
      <div className="container-fluid px-0 py-5" id="wapper-login">
        <div className="content-box">
          <div className="container-fluid">
            <div className="row">
              {changeLogin ? (
                <InputLogin
                  onChangeLogin={this.onChangeLogin}
                  url={url}
                  slug={slug}
                />
              ) : (
                <InputSignIn
                  onChangeLogin={this.onChangeLogin}
                  url={url}
                  slug={slug}
                />
              )}
            </div>
          </div>
        </div>
        <Router>{this.showRouter(routes)}</Router>
      </div>
    );
  }
  showRouter = (routes) => {
    var result = null;

    if (routes.length > 0) {
      result = routes.map((route, index) => {
        return (
          <Route
            key={index}
            path={route.path}
            exact={route.exact}
            component={route.main}
          />
        );
      });
    }
    return <Switch>{result}</Switch>;
  };
}

const routes = [
  {
    path: "/login",
    exact: true,
    main: () => <InputLogin />,
  },
  {
    path: "/register",
    exact: false,
    main: () => <InputSignIn />,
  },
];

const mapStateToProps = (state) => {
  return {
    changeLogin: state.changeLogin,
  };
};

const mapDispatchToProps = (dispatch, props) => {
  return {
    onChangeLogin: () => {
      dispatch(action.onChangeLogin());
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(LoginForm);
