import React, { Component } from "react";
import HeaderRight from "./HeaderRight";
import HeaderLeft from "./HeaderLeft";
import { connect } from "react-redux";
import * as actions from "./../../../../Actions";
import "./styles.scss";

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      toggleNav: false,
    };
  }

  componentDidMount() {
    this.props.onGetUsers();
  }

  onGetAvatar = () => {
    var { users } = this.props;
    var index = JSON.parse(localStorage.getItem("avatar"));
    var avatar = index !== -1 ? users[index].avatar : "";
    return avatar;
  };

  onToggleNav = () => {
    this.setState({
      toggleNav: !this.state.toggleNav,
    });
  };

  render() {
    var { toggleNav } = this.state;

    return (
      <div className="container-fluid" id="header-admin">
        <div className="row align-items-center header-top">
          <HeaderLeft onToggleNav={this.onToggleNav} toggleNav={toggleNav} />
          <HeaderRight users={this.props.users} history={this.props.history} />
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    users: state.usersAdmin,
  };
};

const mapDispatchToProps = (dispatch, props) => {
  return {
    onGetUsers: () => {
      dispatch(actions.acGetUserAdminRequest());
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Header);
