import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import "./styles.scss";
import callApi from "./../../../utils/apiCaller";
import * as actions from "./../../../Actions";
import User from "./User";

const Index = () => {
  // const [usersAdmin, setUsersAdmin] = useState([]);
  const usersAdmin = useSelector((state) => state.usersAdmin);
  const [usersCustomer, setUsersCustomer] = useState([]);
  const dispatch = useDispatch();

  useEffect(() => {
    (async () => {
      callApi("usersAdmin", "GET", null).then((res) =>
        dispatch(actions.acGetUsers(res.data))
      );
      callApi("users", "GET", null).then((res) => setUsersCustomer(res.data));
    })();
  }, []);

  var listUsers = (users) => {
    return users.map((user, index) => {
      return <User user={user} key={index} />;
    });
  };

  return (
    <div className="container-fluid mt-5" id="manager-users">
      <div className="container">
        <div className="row">
          <div className="col-12 text-center">
            <h3 className="font-ubun">Manager Users</h3>
          </div>
          <div className="col-12">
            <table className="table">
              <thead>
                <tr>
                  <th>STT</th>
                  <th>Account</th>
                  <th>Password</th>
                  <th>Status</th>
                  <th>Role</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {listUsers(usersAdmin)}
                {listUsers(usersCustomer)}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Index;
