import React, { useState } from "react";

const Index = (props) => {
  const [hide, setHide] = useState(false);
  const [number, setNumber] = useState(null);

  const changePass = () => {
    var password = props.user.password;
    var passwordHide = "";

    for (let i = 0; i < password.length; i++) {
      passwordHide += "*";
    }

    return passwordHide;
  };

  const onChangeHide = (index) => {
    setHide(!hide);
    setNumber(index);
  };

  const active = (index) => {
    if (number === index) {
      active ? props.user.password : changePass();
    }
  };

  return (
    <tr>
      <td>{props.index}</td>
      <td>{props.user.email}</td>
      {/* <td>{number === props.index ? props.user.password : changePass()}</td> */}
      <td>{active()}</td>
      <td>
        <button
          onClick={() => onChangeHide(props.index)}
          className="btn shadow-none btn-eye fas fa-eye"
        />
      </td>

      <td>
        <span
          className={
            props.user.role
              ? "p-1 bg-warning rounded"
              : "p-1 bg-secondary rounded text-white"
          }
        >
          {props.user.role ? "Admin" : "Customer"}
        </span>
      </td>
      <td>
        <button className="btn shadow-none fas fa-edit"></button>
        <button className="btn shadow-none fas fa-trash"></button>
      </td>
    </tr>
  );
};

export default React.memo(Index);
