import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import "./styles.scss";
import callApi from "./../../../utils/apiCaller";
import * as actions from "./../../../Actions";
import User from "./User";

const Index = () => {
  // const [usersAdmin, setUsersAdmin] = useState([]);
  // const [usersCustomer, setUsersCustomer] = useState([]);
  const dispatch = useDispatch();
  const userAdmin = useSelector((state) => state.usersAdmin);
  const userCustomer = useSelector((state) => state.users);
  var newList = [];

  useEffect(() => {
    dispatch(actions.acGetUserAdminRequest());
    dispatch(actions.acGetUsersRequest());
  }, []);

  useEffect(() => {
    newList = userAdmin.concat(userCustomer);
    return () => {};
  }, [userAdmin, userCustomer]);

  var listUsers = (users) => {
    return users.map((user, index) => {
      return (
        <User
          user={user}
          key={index}
          removeAccount={removeAccount}
          index={index + 1}
        />
      );
    });
  };

  const findIndex = (id, list, role) => {
    var result = -1;

    if (role === "admin") {
      list.map((item, index) => {
        if (item.id === id) {
          return (result = index);
        }
      });
    } else {
      list.map((item, index) => {
        if (item.id === id) {
          return (result = index);
        }
      });
    }

    return result;
  };

  const removeAccount = (id, role) => {
    // if (role === "admin") {
    //   // callApi(`usersAdmin/${id}`, "DELETE", null).then((res) => {
    //   //   console.log(res.data);
    //   // });
    //   var index = findIndex(id, usersAdmin, role);
    //   console.log(index);
    //   return;
    // } else {
    //   var index = findIndex(id, usersCustomer, role);
    //   usersCustomer.splice(index, 1);
    //   console.log(newList);
    //   return;
    // }
  };

  return (
    <div className="container-fluid mt-5" id="manager-users">
      <div className="container">
        <div className="row">
          <div className="col-12 text-center">
            <h3 className="font-ubun">Manager Users</h3>
          </div>
          <div className="col-12">
            <table className="table">
              <thead>
                <tr>
                  <th>STT</th>
                  <th>Email</th>
                  <th colSpan="2">Password</th>
                  <th>Role</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>{listUsers(newList)}</tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Index;
