import React, { Component } from "react";
// import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import * as actions from "../../../Actions";
import "./styles.scss";

class Avatar extends Component {
  constructor(props) {
    super(props);
    this.fileRef = React.createRef();
  }

  onChooseAvatar = () => {
    this.fileRef.current.click();
  };

  render() {
    return (
      <div className="container-fluid avatar-page" id="login-form">
        <div className="login-form-content p-0">
          <div className="row w-100">
            <div className="col-6 pt-5 ">
              <div className="img mb-3 text-center">
                <img
                  src="https://image.flaticon.com/icons/png/128/236/236832.png"
                  alt=""
                  className="avatar rounded-circle img-fluid"
                />
              </div>
              <div className="choose-avatar text-center">
                <input
                  type="file"
                  className="d-none"
                  ref={this.fileRef}
                  name="file"
                />
                <button
                  className="btn shadow-none bg-white"
                  onClick={this.onChooseAvatar}
                >
                  Choose Avatar
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    users: state.usersAdmin,
  };
};

const mapDispatchToProps = (dispatch, props) => {
  return {
    onGetUsers: () => {
      dispatch(actions.acGetUserAdminRequest());
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Avatar);
