import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import * as actions from "./../../../Actions";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";
import "./styles.scss";

const generateProducts = (products) => {
  var aorus = 0;
  var nvidia = 0;
  var amd = 0;
  products.forEach((item) => {
    if (item.aorus) {
      aorus = aorus + 1;
    }
    if (item.nvidia) {
      nvidia = nvidia + 1;
    }
    if (item.amd) {
      amd = amd + 1;
    }
  });
  return {
    chart: {
      type: "column",
    },
    title: {
      text: "Product Statistics",
      style: { "font-size": "30px", "text-transform": "uppercase" },
    },
    xAxis: {
      categories: ["AORUS", "NVIDIA", "AMD"],
    },
    legend: {
      enabled: true,
      title: {
        text: "Statistical chart of product quantity",
        style: { "font-size": "18px" },
      },
    },
    yAxis: [
      {
        title: {
          text: "Products Statistics",
        },
        showFirstLabel: false,
      },
    ],
    series: [
      {
        data: [
          {
            name: "AORUS",
            y: aorus,
            color: "#ff4600",
          },
          {
            name: "NVIDIA",
            y: nvidia,
            color: "#76b900",
          },
          {
            name: "AMD",
            y: amd,
            color: "#a60d13",
          },
        ],
        name: "Amount",
        dataLabel: true,
      },
    ],
    credits: {
      enabled: false,
    },
  };
};

const generateUsers = (users) => {
  return {
    chart: {
      type: "line",
    },
    title: {
      text: "Product Statistics",
      style: { "font-size": "30px", "text-transform": "uppercase" },
    },
    xAxis: {
      type: "datetime",
      dateTimeLabelFormats: {
        month: "%e. %b",
        year: "%b",
      },
      title: {
        text: "Date",
      },
    },
    legend: {
      enabled: true,
      title: {
        text: "Statistical chart of product quantity",
        style: { "font-size": "18px" },
      },
    },
    yAxis: [
      {
        title: {
          text: "Products Statistics",
        },
        showFirstLabel: false,
      },
    ],
    series: [
      {
        data: [10, 20, 30],
        name: "Amount",
        dataLabel: true,
      },
    ],
    credits: {
      enabled: false,
    },
  };
};

export default function HightCharts() {
  const [options, setOptions] = useState({});
  const [optionUsers, setOptionUsers] = useState({});
  const products = useSelector((state) => state.products);
  const users = useSelector((state) => state.users);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(actions.acFetchProductsRequest());
    dispatch(actions.acGetUsersRequest());
  }, []);
  useEffect(() => {
    setOptions(generateProducts(products));
    setOptionUsers(generateUsers(users));
  }, [products, users]);

  return (
    <div className="container">
      <div className="hight-charts">
        <HighchartsReact highcharts={Highcharts} options={options} />
      </div>
      <div className="hight-charts">
        <HighchartsReact highcharts={Highcharts} options={optionUsers} />
      </div>
    </div>
  );
}
