import React, { Component } from "react";

class SearchBar extends Component {

  constructor(props){
    super(props);
    this.state = {
      keyword: "",
    }

  }

  render() {
    return (
      <div className="input-group mb-3 search-bar">
        <input type="search" name="keyword" value={this.state.keyword} onChange={this.onChange} className="form-control rounded input-search" />
        <button onClick={() => this.onSearch()}  className="btn  btn-search fas fa-search"/>
      </div>
    );
  }
}

export default SearchBar;
