import React, { Component } from "react";
import "./styles.scss";
import callApi from "./../../../../utils/apiCaller";
import { connect } from "react-redux";
import * as actions from "./../../../../Actions/index";

class Functions extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: "",
      name: "",
      price: "",
      des: "",
      image: null,
      imageLink: "",
      status: true,
      preview: "",
    };
  }

  componentDidMount() {
    var { match } = this.props;
    var id = match.params.id;
    if (id) {
      callApi(`products/${id}`, "GET", null).then((res) => {
        var product = res.data;
        this.setState({
          id: product.id,
          name: product.name,
          preview: product.image,
          image: product.image,
          imageLink: product.image,
          status: product.status,
        });
      });
    }
  }

  renderInput = (list) => {
    return list.map((item, index) => {
      return;
    });
  };

  onChange = (e) => {
    var target = e.target;
    var name = target.name;
    var value = target.type === "checkbox" ? target.checked : target.value;
    this.setState({
      [name]: value,
    });
  };

  onUpFiles = (e) => {
    this.setState({
      image: e.target.files[0],
    });
    var reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onloadend = () => {
      this.setState({ preview: reader.result, image: reader.result });
    };
  };

  onSave = (e) => {
    e.preventDefault();
    var { history } = this.props;
    var { id, name, price, des, image, status, imageLink } = this.state;
    var product = {};
    if (id) {
      product = {
        id: id,
        name: name,
        price: price,
        des: des,
        image: image ? image : imageLink,
        status: status,
      };
      this.props.onUpdateProduct(product);
      history.goBack();
    } else {
      product = {
        id: id,
        name: name,
        price: price,
        des: des,
        image: image ? image : imageLink,
        status: status,
      };
      this.props.onUpProduct(product);
      history.goBack();
    }
  };

  render() {
    var { id } = this.state;
    return (
      <div className="container mt-3" id="functions">
        <div className="title text-center">
          <div className="display-5 font-family-Ad">
            {id ? "UPDATE PRODUCT" : "ADD PRODUCT"}
          </div>
        </div>
        <form action="" onSubmit={this.onSave} className="form-control">
          <div className="mb-3">
            <label className="label-add" htmlFor="name-product">
              Name:
            </label>
            <input
              type="text"
              id="name-product"
              className="shadow-none form-control"
              name="name"
              value={this.state.name}
              onChange={this.onChange}
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="price-product">
              Price:
            </label>
            <input
              type="number"
              id="price-product"
              className="shadow-none form-control"
              name="price"
              value={this.state.price}
              onChange={this.onChange}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="" className="label-add">
              Graphics Processing:
            </label>
            <input type="text" className="shadow-none form-control" />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="description-product">
              Description:
            </label>
            <textarea
              id=""
              className="w-100"
              cols="30"
              rows="10"
              name="des"
              value={this.state.des}
              onChange={this.onChange}
            ></textarea>
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="img-product">
              Image Address:
            </label>
            <input
              type="file"
              id="img-product"
              className="shadow-none form-control"
              multiple
              onChange={this.onUpFiles}
            />
          </div>
          <div className="mb-3">
            <label className="label-add" htmlFor="img-product">
              Image Link:
            </label>
            <input
              type="text"
              id="img-product"
              className="shadow-none form-control"
              name="imageLink"
              multiple
              value={this.state.imageLink}
              onChange={this.onChange}
            />
          </div>
          <img src={this.state.preview} alt="" />
          <div className="mb-3">
            <label className="label-add me-2" htmlFor="status">
              Status
            </label>
            <input
              type="checkbox"
              className=""
              name="status"
              value={this.state.status}
              onChange={this.onChange}
              id="status"
              checked={this.state.status}
            />
          </div>
          <div className="mb-3">
            <button
              onClick={() => this.props.history.push("/admin")}
              className="btn btn-primary me-3 mb-3"
            >
              Go Back
            </button>
            <button type="submit" className="btn btn-success mb-3">
              Save
            </button>
          </div>
        </form>
      </div>
    );
  }
}

const specification = [
  {
    label: "Graphics Processing",
    name: "gp",
  },
  {
    label: "Core Clock",
    name: "cc",
  },
  {
    label: "SLI Support",
    name: "ss",
  },
  {
    label: "Output",
    name: "o",
  },
  {
    label: "Power Connectors",
    name: "pc",
  },
  {
    label: "Recommended PSU",
    name: "rp",
  },
  {
    label: "OpenGL",
    name: "op",
  },
  {
    label: "DirectX",
    name: "d",
  },
  {
    label: "PCB Form",
    name: "pf",
  },
  {
    label: "Card Size",
    name: "cs",
  },
  {
    label: "Multi-view",
    name: "mv",
  },
  {
    label: "Digital max resolution",
    name: "dmr",
  },
  {
    label: "Card Bus",
    name: "cb",
  },
  {
    label: "Memory Bandwidth (GB/sec)",
    name: "mbGB",
  },
  {
    label: "Memory Bus",
    name: "mb",
  },
  {
    label: "Memory Type",
    name: "mt",
  },
  {
    label: "Memory Size",
    name: "msize",
  },
  {
    label: "Memory Clock",
    name: "mc",
  },
  {
    label: "CUDA® Cores",
    name: "ccores",
  },
  {
    label: "Accessories",
    name: "a",
  },
];

const mapDispatchToProps = (dispatch, props) => {
  return {
    onUpProduct: (product) => {
      dispatch(actions.acUpProductRequest(product));
    },
    onUpdateProduct: (product) => {
      dispatch(actions.acUpdateProductRequest(product));
    },
  };
};

export default connect(null, mapDispatchToProps)(Functions);
