import React, { Component } from "react";
import InputSignIn from "./InputSignIn/InputSignIn";
import "./styles.scss";
import { connect } from "react-redux";
import * as action from "./../../../../Actions/index";

class SignInForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      info: [],
      err: {},
    };
  }

  onChangeLogin = () => {
    this.props.onChangeLogin();
  };

  handleErr = () => {
    var { info } = this.state;
    var err = {};
    // Check Email
    if (!info.email) {
      err.email = "The Email field is required";
    } else if (
      info.email.lastIndexOf("@") === -1 ||
      info.email.lastIndexOf(".") === -1
    ) {
      err.email = "The Email must be a valid email address";
    }

    //Check NickName
    if (!info.username) {
      err.username = "The NickName field is required";
    } else if (info.username.indexOf(" ") >= 0) {
      err.username = "Space is not allowed";
    } else if (info.username.length >= 30) {
      err.username = "Nickname must be widthin 30 characters";
    }

    //Check Password
    if (!info.password) {
      err.password = "The Password field is required";
    } else if (info.password.indexOf(" ") >= 0) {
      err.password = "Space is not allowed";
    } else if (info.password.length < 8 || info.password.length > 20) {
      err.password = "Password must be 8 to 20 characters";
    }

    // Check Re-Password
    if (!info.repassword) {
      err.repassword = "The Re-Enter Password field is required";
    } else if (info.repassword !== info.password) {
      err.repassword = "Enter back not match password";
    }

    this.setState({
      err,
    });
  };

  onRegister = (data) => {
    var { info } = this.state;
    this.handleErr();
    info.push(data);
    this.setState({
      info,
    });
  };

  render() {
    var { changeLogin, match } = this.props;
    var url = match.url;
    var slug = changeLogin ? "register" : "login";
    return (
      <div className="container-fluid px-0 py-5" id="wapper-login">
        <div className="content-box">
          <div className="container-fluid">
            <div className="row">
              <InputSignIn
                onChangeLogin={this.onChangeLogin}
                url={url}
                slug={slug}
                onRegister={this.onRegister}
                err={this.state.err}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    changeLogin: state.changeLogin,
    listRegister: state.register,
  };
};

const mapDispatchToProps = (dispatch, props) => {
  return {
    onChangeLogin: () => {
      dispatch(action.onChangeLogin());
    },
    onRegister: () => {
      dispatch(action.acOnRegister());
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(SignInForm);
