import React, { useState, useEffect } from "react";

const Index = (props) => {
  // const [number, setNumber] = useState(null);
  const [changePassword, setChangePassword] = useState("");
  const [clicked, setClicked] = useState(false);
  const [show, setShow] = useState(false);

  useEffect(() => {
    setChangePassword(props.user.password);
  }, []);

  const changePass = () => {
    var password = changePassword;
    var passwordHide = "";

    for (let i = 0; i < password.length; i++) {
      passwordHide += "*";
    }

    return passwordHide;
  };

  // const onChangeHide = (index) => {
  //   setNumber(index);
  // };

  return (
    <tr>
      <td>{props.index}</td>
      <td>{props.user.email}</td>
      {/* <td>
        {clicked ? (
          <input
            type="text"
            onChange={(e) => setChangePassword(e.target.value)}
            name="password"
            value={changePassword}
            className="form-control"
          />
        ) : number === props.index ? (
          changePassword
        ) : (
          changePass()
        )}
      </td> */}
      <td>
        <button
          onClick={() => setShow(!show)}
          className="btn shadow-none btn-eye fas fa-eye"
        />
      </td>
      <td>
        <span
          className={
            props.user.role
              ? "p-1 bg-warning rounded"
              : "p-1 bg-secondary rounded text-white"
          }
        >
          {props.user.role ? "Admin" : "Customer"}
        </span>
      </td>
      <td>
        <button
          onClick={() => setClicked(!clicked)}
          className="btn shadow-none fas fa-edit"
        ></button>
        <button
          onClick={() => props.removeAccount(props.user.id, props.user.role)}
          className="btn shadow-none fas fa-trash"
        ></button>
      </td>
    </tr>
  );
};

export default Index;
