import React, { useState, useEffect } from "react";
import SearchIcon from "@material-ui/icons/Search";
import ViewQuiltIcon from "@material-ui/icons/ViewQuilt";
import NotificationsIcon from "@material-ui/icons/Notifications";
import PersonIcon from "@material-ui/icons/Person";
import MenuIcon from "@material-ui/icons/Menu";
import { useLocation, useHistory } from "react-router-dom";

import {
  TextField,
  InputAdornment,
  Menu,
  MenuItem,
  Hidden,
  Avatar,
} from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";
import { useSelector, useDispatch } from "react-redux";
import { acGetUserAdminRequest } from "./../../../../Actions";

export default function HeaderNav(props) {
  const [anchorEl, setAnchorEl] = useState(null);
  const handleClose = () => {
    setAnchorEl(null);
  };
  const usersAdmin = useSelector((state) => state.usersAdmin);
  const dispatch = useDispatch();
  let history = useHistory();
  const [title, setTitle] = useState("");
  const [avatar, setAvatar] = useState(null);
  const location = useLocation();

  useEffect(() => {
    switch (location.pathname) {
      case "/admin":
        setTitle("Dashboard");
        break;
      case "/admin/profile":
        setTitle("Profile");
        break;
      case "/admin/users":
        setTitle("users");
        break;
      case "/admin/list-products":
        setTitle("Products");
        break;
      case "/admin/typography":
        setTitle("typography");
        break;
      case "/admin/icons":
        setTitle("icons");
        break;
      case "/admin/maps":
        setTitle("maps");
        break;
      case "/admin/notifications":
        setTitle("notifications");
        break;
      default:
        break;
    }
  }, [location]);

  useEffect(() => {
    dispatch(acGetUserAdminRequest());
    let id = localStorage.getItem("uid");
    if (usersAdmin.length > 0) {
      let index = usersAdmin.findIndex((user) => user.id === id);
      // console.log(index);
      // setAvatar(usersAdmin[index].avatar || "");
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("uid");
    history.push("/login-admin");
  };

  return (
    <>
      <Grid
        container
        spacing={0}
        className="bgr-admin px-3"
        alignItems="center"
      >
        <Grid item xs={6}>
          <Button className="header-nav-title color-black">{title}</Button>
        </Grid>
        <Hidden mdUp={true}>
          <Grid
            item
            xs={6}
            className="d-flex"
            flexDirection="row"
            alignItems="center"
            justifyContent="flex-end"
          >
            <Button
              onClick={() => props.onToggleSide()}
              sx={
                props.toggleSide
                  ? {
                      backgroundColor: "#eeee",
                      transition: "all 0.5s",
                      marginRight: "260px",
                    }
                  : { backgroundColor: "#eeee", transition: "all 0.5s" }
              }
            >
              <MenuIcon sx={{ color: "black" }} />
            </Button>
          </Grid>
        </Hidden>
        <Hidden mdDown={true}>
          <Grid
            item
            xs={6}
            className="d-flex"
            flexDirection="row"
            alignItems="center"
            justifyContent="flex-end"
          >
            <TextField
              id="standard-basic"
              label="Search"
              variant="standard"
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <SearchIcon />
                  </InputAdornment>
                ),
              }}
            />
            <Button href="/admin" className="color-black">
              <ViewQuiltIcon />
            </Button>
            <Button className="color-black">
              <NotificationsIcon />
            </Button>
            <Button
              onClick={(e) => setAnchorEl(e.currentTarget)}
              className="color-black"
            >
              {avatar ? <Avatar src={avatar} /> : <PersonIcon />}
            </Button>
            <Menu
              id="menu-user"
              anchorEl={anchorEl}
              keepMounted
              open={Boolean(anchorEl)}
              onClose={handleClose}
            >
              <MenuItem onClick={handleClose}>Profile</MenuItem>
              <MenuItem onClick={handleClose}>Settings</MenuItem>
              <MenuItem onClick={handleLogout}>Logout</MenuItem>
            </Menu>
          </Grid>
        </Hidden>
      </Grid>
    </>
  );
}
