import React, { Component } from "react";
import { Link } from "react-router-dom";
import "./styles.scss";
import { connect } from "react-redux";

class Footer extends Component {
  renderIcon = (list) => {
    var result = null;

    result = list.map((item, index) => {
      return <Link to={item.to}>{item.item}</Link>;
    });

    return result;
  };

  renderSub = (list) => {
    var result = null;

    result = list.map((item, index) => {
      return (
        <li className="nav-item">
          <Link to="/" className="nav-link size-15 text-white">
            Motherboards
          </Link>
        </li>
      );
    });

    return result;
  };

  renderList = (nav) => {
    var result = null;

    result = nav.map((item, index) => {
      return (
        <div className="col">
          <div className="h5 text-white text-center">{item.navItem}</div>
          <ul className="nav justify-content-center">
            {this.renderSub(item.list)}
          </ul>
        </div>
      );
    });

    return result;
  };

  render() {
    return (
      <div className="container-fluid py-4 pb-3" id="footer-home">
        <div className="container">
          <div className="row">
            <div className="col-12 text-center">
              <img
                src="https://www.aorus.com/assets/img/logo_svg.2fc451ee.svg"
                alt=""
              />
            </div>
            <div className="col-12 my-3 size-18 text-white text-center">
              TEAM UP. FIGHT ON.
            </div>
            <div className="col-12 text-center">
              {this.renderIcon(listIcon)}
            </div>
          </div>
          <div className="row">
            <div className="col ">
              <div className="h5 text-white text-center">ABOUT US</div>
              <ul className="nav justify-content-center">
                <li className="nav-item">
                  <Link to="/" className="nav-link size-15 text-white">
                    About AROUS
                  </Link>
                  <Link to="/" className="nav-link size-15 text-white">
                    About GIGABYTE
                  </Link>
                </li>
              </ul>
            </div>
            <div className="col">
              <div className="h5 text-white text-center">PRODUCTS</div>
              <ul className="nav justify-content-center">
                <li className="nav-item">
                  <Link to="/" className="nav-link size-15 text-white">
                    Motherboards
                  </Link>
                  <Link to="/" className="nav-link size-15 text-white">
                    Graphics Cards
                  </Link>
                  <Link to="/" className="nav-link size-15 text-white">
                    Laptops
                  </Link>
                  <Link to="/" className="nav-link size-15 text-white">
                    Monitors
                  </Link>
                  <Link to="/" className="nav-link size-15 text-white">
                    Desktop PC
                  </Link>
                  <Link to="/" className="nav-link size-15 text-white">
                    PC Peripherals
                  </Link>
                  <Link to="/" className="nav-link size-15 text-white">
                    PC Components
                  </Link>
                </li>
              </ul>
            </div>
            <div className="col text-center">
              <div className="h5 text-white">PRODUCTS</div>
              <ul className="nav justify-content-center">
                <li className="nav-item">
                  <Link to="/" className="nav-link size-15 text-white">
                    News
                  </Link>
                  <Link to="/" className="nav-link size-15 text-white">
                    Events
                  </Link>
                  <Link to="/" className="nav-link size-15 text-white">
                    Blog
                  </Link>
                  <Link to="/" className="nav-link size-15 text-white">
                    Wallpaper
                  </Link>
                </li>
              </ul>
            </div>
            <div className="col text-center">
              <div className="h5 text-white">PRODUCTS</div>
              <ul className="nav justify-content-center">
                <li className="nav-item">
                  <Link to="/" className="nav-link size-15 text-white">
                    Motherboards
                  </Link>
                  <Link to="/" className="nav-link size-15 text-white">
                    Motherboards
                  </Link>
                </li>
              </ul>
            </div>
            <div className="col text-center">
              <div className="h5 text-white">PRODUCTS</div>
              <ul className="nav justify-content-center">
                <li className="nav-item">
                  <Link to="/" className="nav-link size-15 text-white">
                    Motherboards
                  </Link>
                  <Link to="/" className="nav-link size-15 text-white">
                    Motherboards
                  </Link>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const listIcon = [
  {
    to: "/",
    item: <i className=" fab fa-facebook-f" />,
  },
  {
    to: "/",

    item: <i className="fab fa-twitter" />,
  },
  {
    to: "/",

    item: <i className="fab fa-youtube" />,
  },
  {
    to: "/",

    item: <i className="fab fa-instagram" />,
  },
];

const mapStateToProps = (state) => {
  return {
    nav: state.navItem,
  };
};

export default connect(mapStateToProps, null)(Footer);
