import React, { Component } from "react";
import "./styles.scss";
import { connect } from "react-redux";
import * as actions from "./../../../Actions/index";
import DetailNav from "./../DetailNav/DetailNav";
import Breadcumb from "./../../LoginPage/ContentLogin/Breadcumb/Breadcumb";

class Specification extends Component {
  constructor(props) {
    super(props);
    this.state = {
      sticky: false,
    };
  }

  componentDidMount() {
    this.props.onGetProducts();
    window.addEventListener("scroll", this.sticky);
  }

  sticky = () => {
    if (window.scrollY > 172) {
      this.setState({ sticky: true });
    } else {
      this.setState({ sticky: false });
    }
  };

  findIndex = (list, id) => {
    var result = null;

    result = list.forEach((item, index) => {
      if (item.id === id) {
        return (result = index);
      }
    });

    return result;
  };

  render() {
    var { match, products } = this.props;
    var index = findIndex(products, match.params.id);
    return (
      <div className="container-fluid p-0">
        <Breadcumb
          to="/"
          toBack="Home"
          to1="/GraphicsCard"
          toBack1="Graphics Cards"
          current="AORUS GeForce RTXâ„¢ 3090 XTREME WATERFORCE 24G"
          bgr="bgr-black"
        />
        <DetailNav sticky={this.state.sticky} match={match} />
        <div className="container-fluid specification pt-5">
          <div className="container">
            <div className="row">
              <div className="col-12 mb-5 text-center">
                <h5 className="color-orange">
                  AORUS GeForce RTXâ„¢ 3090 XTREME WATERFORCE 24G
                </h5>
              </div>
              <div className="col-12">
                <table className="table">
                  <tbody>
                    <tr>
                      <td width="30%">Graphics Processing</td>
                      <td width="70%">GeForce RTXâ„¢ 3090</td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    products: state.products,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onGetProducts: () => {
      dispatch(actions.acFetchProductsRequest());
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Specification);
