import React, { Component } from "react";
import HeaderRight from "./HeaderRight";
import HeaderLeft from "./HeaderLeft";
import { connect } from "react-redux";
import * as actions from "./../../../../Actions";
import callApi from "./../../../../utils/apiCaller";
import "./styles.scss";

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      toggleNav: false,
      avatar: "",
    };
  }

  componentDidMount() {
    this.props.onGetUsers();
  }

  getAvatar = () => {
    var { users } = this.props;
    var userMail = JSON.parse(localStorage.getItem("user"));
    var index = this.findIndex(userMail, users);
    callApi(`usersAdmin/${users[index].id}`, "GET", null).then((res) => {
      var user = res.data;
      this.setState({
        avatar: user.avatar,
      });
    });
  };

  findIndex = (item, list) => {
    var result = -1;

    list.forEach((user, index) => {
      console.log(user.email);
      if (user.email === item) {
        console.log(index);
        return (result = index);
      }
    });

    return result;
  };

  onToggleNav = () => {
    this.setState({
      toggleNav: !this.state.toggleNav,
    });
  };

  render() {
    var { toggleNav, avatar } = this.state;
    return (
      <div className="container-fluid" id="header-admin">
        <div className="row align-items-center header-top">
          <HeaderLeft onToggleNav={this.onToggleNav} toggleNav={toggleNav} />
          <HeaderRight avatar={avatar} history={this.props.history} />
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    users: state.usersAdmin,
  };
};

const mapDispatchToProps = (dispatch, props) => {
  return {
    onGetUsers: () => {
      dispatch(actions.acGetUserAdminRequest());
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Header);
